mapper.select:
SELECT TRIP_COMPANION_NO, TRIP_PROJECT_NO, TRIP_COMPANION_NM, TRIP_COMPANION_ORDER, TRIP_COMPANION_REG_DT, TRIP_COMPANION_MOD_DT FROM TRIP_COMPANION
AND TRIP_PROJECT_NO = #{tripProjectNo}
AND TRIP_COMPANION_ORDER = #{tripCompanionOrder}

update.foreach:
UPDATE TRIP_COMPANION SET TRIP_COMPANION_NM = #{item.tripCompanionNm}, TRIP_COMPANION_ORDER = #{item.tripCompanionOrder}, TRIP_COMPANION_REG_DT = sysdate(), TRIP_COMPANION_MOD_DT = sysdate()
AND TRIP_PROJECT_NO = #{item.tripProjectNo}
AND TRIP_COMPANION_ORDER = #{item.tripCompanionOrder}

mapper.delete:
DELETE
  FROM TRIP_COMPANION
 WHERE TRIP_PROJECT_NO = #{tripProjectNo}

    AND TRIP_COMPANION_ORDER = #{tripCompanionOrder}
