mapper.select:
SELECT TRIP_FAVORITES_NO, TRIP_PROJECT_NO, TRIP_FAVORITES_NM, TRIP_FAVORITES_EXPLAN, TRIP_FAVORITES_ORDER, TRIP_FAVORITES_OUT_ID, TRIP_FAVORITES_IMG_URL FROM TRIP_FAVORITES
AND TRIP_PROJECT_NO = #{tripProjectNo}
AND TRIP_FAVORITES_OUT_ID = #{tripFavoritesOutId}

mapper.delete:
DELETE FROM TRIP_FAVORITES


        AND TRIP_PROJECT_NO = #{tripProjectNo}


        AND TRIP_FAVORITES_OUT_ID = #{tripFavoritesOutId}
